mapper.select:
select ba.id from
  buyer_account ba 


  ba.nick = #{nick} and ba.platform = #{platform}

  or (
    ba.user_id = #{userId} and ba.platform = #{platform}

	  and id != #{id}

    and (
      ba.consignee = #{consignee}
      or ba.mobile = #{mobile} 
      or (
        ba.state = #{state} 
        and ba.city = #{city} 

        and region = #{region}

        and ba.address = #{address} 
      	)
   	  )
  	) 

limit 1

select count(1) from buyer_account


		user_id = #{userId}


		and platform = #{platform}


		and status = #{status}


		and id = #{accountId}

select
	ba.id, ba.nick, ba.platform, ba.state, ba.city, ba.region, ba.address,
	ba.mobile, ba.status, ba.consignee,ba.memo, ba.create_time, ba.modify_time

	,u.nick userNick

from buyer_account ba

	join user u on u.id = ba.user_id



		ba.user_id = #{userId}


		and ba.platform = #{platform}


		and ba.status = #{status}


		and ba.id = #{accountId}


		and u.nick like '%${userNick}%'


		and ba.nick like '%${accountNick}%'


	order by order_number

	limit #{startIndex},#{pageSize}

mapper.update:
update buyer_account set
modify_time = #{modifyTime}

	,nick = #{nick}


	,consignee = #{consignee}


	,state = #{state}


	,city = #{city}


	,region = #{region}


	,address = #{address}


	,mobile = #{mobile}


	,status = #{status}


	,memo = #{memo}

where id = #{id}

update buyer_account set 

	nick = #{nick}


	,
	consignee = #{consignee}


	,
	state = #{state}


	,
	city = #{city}


	,
	region = #{region}


	,
	address = #{address}


	,
	mobile = #{mobile}

where id = #{id}
